        public static long PuzzleA(string input)
        {
            long floor = 0;

            for (int i = 0; i < input.Length; i++)
            {
                char item = input[i];
                floor = UpdateFloor(item, floor);
            }

            return floor;
        }

        public static long PuzzleB(string input)
        {
            long floor = 0;

            for (int i = 0; i < input.Length; i++)
            {
                char item = input[i];
                floor = UpdateFloor(item, floor);

                if (floor < 0)
                    return i + 1;
            }

            return -1;
        }

        private static long UpdateFloor(char input, long result)
        {
            if (input == '(')
                result++;
            else if (input == ')')
                result--;

            return result;
        }
