        public static long Day_01_A(string input)
        {
            long floor = 0;

			for (int i = 0; i < input.Length; i++)
            {
				char item = input[i];
				floor = Day_01_Sub(item, floor);
			}

            return floor;
		}

		public static long Day_01_B(string input)
		{
			long floor = 0;

			for (int i = 0; i < input.Length; i++)
			{
				char item = input[i];
				floor = Day_01_Sub(item, floor);

				if (floor < 0)
					return i + 1;
			}

			return -1;
		}

		private static long Day_01_Sub(char input, long result)
		{
			if (input == '(')
				result++;
			else if (input == ')')
				result--;

			return result;
		}
	}
